class Solution {
public:
    int maxOperations(string s) {
        // countNumberOfOneBeforeZero
        int n = s.size();
        int ones = 0, ans = 0;
        for (int i = 0; i < n; i++) {
            if(i > 0 && s[i] == '0' && s[i-1] == '0'){
                continue;
            }
            if (s[i] == '1') {
                ones++;
            } else {
                ans += ones;
            }
        }

        return ans;
    }
};
