class Solution {
public:
    int maxOperations(string s) {
        int res=0;
        int cnt=0;
        bool check=true;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'){
                cnt++;
                check=true;
            }
            else{
                if(check) res+=cnt;
                check=false;
            }
        }
        return res;
    }
};
