class Solution {
public:
    int maxOperations(string s) {
        const int n = s.size();

        int pre = 0, ans = 0;
        for (int i = 0; i < n; ) {
            if (i < n - 1 && s[i] == '1' && s[i + 1] == '0') {
                int j = i + 1;
                while (j < n - 1 && s[j + 1] == '0') {
                    ++j;
                }
                ans += ++pre;
                i = j + 1;
            } else if (s[i] == '1') {
                ++pre;
                ++i;
            } else {
                ++i;
            }
        }

        return ans;
    }
};
