class Solution {
public:
    int maxOperations(std::string s) {
        int ans = 0;
        int temp = 0;
        bool is_there_One = false;
        vector<int> result;
        int i = 0;
        std::ios_base::sync_with_stdio(false);
        std::cin.tie(nullptr);
        std::cout.tie(nullptr);
        while (i < s.length()) {
            char ch = s[i];
            if (ch == '1') {
                result.push_back(1);
                is_there_One = true;
            } else if (ch == '0') {
                if (is_there_One) {
                    result.push_back(0);
                    is_there_One = false;
                }
            }
            i++;
        }
        std::ios_base::sync_with_stdio(false);
        std::cin.tie(nullptr);
        std::cout.tie(nullptr);
        int j = 0;
        while (j < result.size()) {
            if (result[j] == 1) {
                temp++;
            } else {
                ans += temp;
            }
            j++;
        }
        std::ios_base::sync_with_stdio(false);
        std::cin.tie(nullptr);
        std::cout.tie(nullptr);
        return ans;
    }
