class Solution {
public:
    int secret(vector<int>&bits){
        int ans = 0,p = 0;
        for (int bit : bits) {
            if (bit == 1) {
                p += 1;
            } else {
                ans += p;
            }
        }

        return ans;
    }
    int maxOperations(string s) {

        bool has_one = false;
        vector<int> bits;

        for (char ch : s) {
            if (ch == '1') {
                bits.push_back(1);
                has_one = true;
            } else if (ch == '0' && has_one) {
                bits.push_back(0);
                has_one = false;
            }
        }
        return secret(bits);

    }
};
