class Solution {
public:
    int maxOperations(string s) {
        int n = s.length();
        int temp = 0;
        int op= 0;
        vector<int> bits;
        bool flg = 0;

        for(auto &c: s)
        {
            if(c == '1')
            {
                bits.push_back(1);
                flg = 1;
            }else if(c == '0' && flg)
            {
                bits.push_back(0);
                flg = 0;
            }
        }

        for(auto &bit: bits)
        {
            if(bit == 1)    temp++;
            else    op += temp;
        }

        return op;
    }
};
