class Solution {
public:
    int maxOperations(string s) {
        int operations = 0, onesCount = 0, temp = 0; 
        bool hasOne = 0; 
        vector<int> arr; 
        for(int i = 0; i < s.size(); i++){ 
            if(s[i] == '1'){ 
                arr.push_back(1); 
                hasOne = 1; 
            } 
            if(s[i] == '0' and hasOne){ 
                arr.push_back(0); 
                hasOne = 0; 
            } 
        } 
        for(int i=0;i< arr.size();i++){ 
            if(arr[i]){ 
                onesCount++; 
            } else { 
                operations += onesCount; 
            } 
        } 
        return operations; 
    }
};
