import java.util.Vector;

class Solution {
    public int maxOperations(String s) {
        int totalOperations = 0;
        int countOnes = 0;
        boolean encounteredOne = false;
        Vector<Integer> significantBits = new Vector<>();

        for (char ch : s.toCharArray()) {
            if (ch == '1') {
                significantBits.add(1);
                encounteredOne = true;
            } else if (ch == '0' && encounteredOne) {
                significantBits.add(0);
                encounteredOne = false;
            }
        }

        for (int bit : significantBits) {
            if (bit == 1) {
                countOnes++;
            } else {
                totalOperations += countOnes;
            }
        }

        return totalOperations;
    }
}
