class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, c1 = 0, n = s.size();
        for(int i = 0; i < n; i++) {
            if(s[i] == '1') c1++;
            else {
                while(i < n && s[i] == '0') i++;  
                ans += c1; i--;
            }
        }
        return ans;
    }
};
