class Solution {
    public int maxOperations(String s) {
        int len = s.length();
        int oB=0;
        int count=0;
        int ans=0;
        int oC=0;
        for(int i=len-1;i>=0;i--)
        {
            if(s.charAt(i)=='1')
            {
                count++;
                oC++;
            }
            else{
                ans+=count*oB;

                if(count>0)
                oB++;
                count=0;
            }
        }
      //  System.out.println(ans);
        if(s.charAt(len-1)=='1')
        return count*oB + ans;
        return count*oB+ans+oC;
    }
}
