class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)

        res = 0
        pre = 0

        i = 0
        while i < n:
            while i < n and s[i] == '0':
                i += 1

            res += pre

            while i < n and s[i] == '1':
                i += 1
                pre += 1

        return res
