class Solution:
    def maxOperations(self, s: str) -> int:
        operations = 0
        one_count = 0
        found_one = False
        bit_sequence = []

        for char in s:
            if char == '1':
                bit_sequence.append(1)
                found_one = True
            elif char == '0' and found_one:
                bit_sequence.append(0)
                found_one = False

        for bit in bit_sequence:
            if bit == 1:
                one_count += 1
            else:
                operations += one_count

        return operations
