class Solution {
public:
    int maxOperations(string s) {
        /*
                int ops = 0;

                j = n-1

                i=n-1 start do i--

                if(1 at i) ops+=(j-i);

                -----

                start from left

                int cnt = 0;

                whn i get 1 first time cnt++; j = i

                whn get 1 ops+=(i - j -1)*cnt   j = i



        */
        int n = s.length();
        int i = 0;
        int j = -1;
        int res = 0;
        int cnt = 0;
        while(i<n && j<n){
            if(s[i]=='1' && j==-1){
                cnt++;
                j=i;
            }
            else if(s[i]=='1'){
                if((i-j-1)>0){
                    res+=cnt;
                }
                cnt++;
                j=i;
            }
            i++;
        }
        if((i-j-1)>0){
            res+=cnt;
        }
        cnt++;
        j=i;
        return res;
    }
};
