class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int ans = 0;
        int c1 = 0;
        for(int i = 0; i < n; i++) {
            if(s[i] == '0') {
                while(i < n && s[i] == '0') i++;
                ans += c1;
            }else {
                while(i < n && s[i] == '1') i++, c1++;
            }
            i--;
        }
        return ans;
    }
};
