class Solution {
public:
    int maxOperations(string s) {
        int ans = 0 ;
        int n = s.length();
        vector<int> sum(n);
        if(s[0] == '1') sum[0] = 1;
        for(int i = 1; i < n; i++){
            if(s[i] == '1') sum[i] = sum[i - 1] + 1;
            else sum[i] = sum[i - 1];
        }
        for(int i = n - 1; i >= 0; i--){
            if(i > 0 && s[i - 1] == '1' && s[i] == '0') ans += sum[i];
        }
        return ans;
    }
};
