class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)
        idx = [i for i, ch in enumerate(s) if ch == '1']
        idx.append(n)
        ans, cnt = 0, 1
        for a, b in pairwise(idx):
            if b - a > 1:
                ans += cnt
            cnt += 1
        return ans
