class Solution {
public:
    int maxOperations(string s) {
        int count = 0;
        bool new_group = false;
        int group_count = 0;
        for (int i = s.size()-1; i >= 0; i--) {
            if (s[i] == '0')
                new_group = true;
            else {
                if (new_group)
                    group_count++;
                count += group_count;
                new_group = false;
            }
        }
        return count;
    }
};
