class Solution:
    def maxOperations(self, s: str) -> int:
        check = [i for i in range(len(s)) if s[i] == '1']
        if check == []:
            return 0
        else:
            n = len(check)
            if n == 1:
                return 1 if check[0] != len(s) - 1 else 0
            else:
                ans = 0
                step = 1
                for i in range(1,n):
                    if check[i] - check[i - 1] > 1:
                        ans += step
                        step += 1
                    else:
                        step += 1
                else:
                    return ans if check[-1] == len(s) - 1 else ans + n
