class Solution {
public:
    int maxOperations(string s) {
        int operationsCount = 0, consecutiveOnesCount = 0;
        bool foundOne = false;
        vector<int> processedBits;

        for(int i = 0; i < s.size(); i++) {
            if(s[i] == '1') {
                processedBits.push_back(1);
                foundOne = true;
            }
            if(s[i] == '0' && foundOne == true) {
                processedBits.push_back(0);
                foundOne = false;
            }
        }

        for(auto bit : processedBits) {
            if(bit == 1) {
                consecutiveOnesCount++;
            } else {
                operationsCount += consecutiveOnesCount;
            }
        }
        return operationsCount;
    }
};
