class Solution {
public:
    int maxOperations(string s) {
        int c=0, ans=0;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'){
                c++;
            }
            else{
                while(i<s.size() && s[i]=='0'){
                    i++;
                }
                i--;
                ans += c;
            }
        }
        return ans;
    }
};
