class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int i=0;
        int n = s.size();
        int t = 0;
        int c1 = 0;
        int sum = 0;
        while(i<n){
            if(s[i]=='1'){
                t++;
                i++;
            }
            else{
                c1+=t;
                sum+=c1;
                t=0;
                while(i<n && s[i]=='0'){
                    i++;
                }


            }

        }
        return sum;
    }
};
