class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int count = 0;
        int max_operations = 0;
        if(s[0] == '1') count = 1;
        for(int i = 1;i<n;i++){
            if(s[i] == '1' && s[i-1] == '0'){
                max_operations+=count;
                count++;
            }
            else if(s[i] == '1'){
                count++;
            }

        }
        if(s[n-1] == '0') max_operations += count;
        return max_operations;
    }
};
