class Solution {
public:
    int maxOperations(string str) {
        unordered_map<int,int>mpp;
        vector<int> looks;
        int answer = 0;
        int parit = 0;
        int rain = 0;
        bool flag = false;

        for(auto it:str){
            mpp[it]++;
        }

        for (int k = 0; k < str.size(); k++) {
            if (flag == true && str[k] == '0' ) {
                looks.push_back(0);
                flag = false;
            }
            if (str[k] == '1') {
                looks.push_back(1);
                flag = true;
            }

        }
        for (auto yo : looks) {
            if (yo == 0) {
                answer += parit;
            } else {
                parit++;
            }
        }

        return answer;
    }
};
