class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, count = 0;
        bool flag = true;
        int n = s.size(), i = 0;
        while(i < n) {
            if (s[i] == '1') {
                count++;
                flag = true;
            } 
            else {
                if (flag) {
                    ans += count;
                }
                flag = false;
            }
            i++;
        }
        return ans;
    }
};
