class Solution:
    def maxOperations(self, s: str) -> int:

        basons = groupby(s)

        basis = [(i, list(k)) for i,k in basons]


        ans = 0
        chugg = 0
        for ea in range(len(basis)):
            if basis[ea][0] == '1':
                chugg += len(list(basis[ea][1]))
            else:
                ans += chugg

        return ans
