class Solution:
    def maxOperations(self, s: str) -> int:
        k,c,c1=0,0,0
        l=[]
        for i in s:
            if i=='1':
                l.append(1)
                c1=1
            if c1==1 and i=='0':
                l.append(0)
                c1=0
        for i in l:
            if i==1:
                k+=1
            else:
                c+=k
        return c
