class Solution {
public:
    int maxOperations(string s) {
          int ans = 0, ones = 0;
        bool oneFound = false;
        vector<int> seq;

        for (char c : s) {
            if (c == '1') {
                seq.push_back(1);
                oneFound = true;
            }
            if (c == '0' && oneFound) {
                seq.push_back(0);
                oneFound = false;
            }
        }

        for (int num : seq) {
            if (num == 1) {
                ones++;
            } else {
                ans += ones;
            }
        }

        return ans;
    }
};
