class Solution {
public:
    int maxOperations(string s) {
        int cnt=0,ans=0,n=s.size();
        if(s[0]=='1') cnt=1;

        for(int i=1;i<s.size();i++){
            if(s[i]=='1' && s[i-1]=='0'){
                ans+=cnt;
                cnt++;
            } else if(s[i]=='1') cnt++;
        }

        if(s[n-1]=='0') ans+=cnt;

        return ans;
    }
};
