class Solution {
    public int maxOperations(String s) {

        int oneCnt = 0;
        int ans = 0;
        for(int i = 0; i < s.length(); i++) {
            char c = s.charAt(i);
            if(c == '1') {
                if(i > 0 && s.charAt(i-1) == '0') {
                    ans += oneCnt;
                }
                oneCnt++;
            }
        }

        if(s.charAt(s.length() - 1) == '0') {
            ans += oneCnt;
        }

        return ans;
    }
}
