class Solution {
public:
    int maxOperations(string s) {
        int cnt=0, one=0;
        int n=s.size(), i=0;
        while(i<n){
            if(s[i]=='1')one++;
            else{
                while(i<n && s[i]=='0'){
                    i++;
                }
                i--;
                cnt+=one;
            }

            i++;
        }
        return cnt;
    }
};
