class Solution {
    public int maxOperations(String s) {
        int arr[] = new int[s.length()];
        int c = 0, pc = 0;
        for(int i = s.length() - 1; i >= 0; i--) {
            if(s.charAt(i) == '0') c++;
            else {
                if(c > 0) {
                    arr[i] = pc + 1;
                    pc = pc + 1;
                } else arr[i] = pc;
                c = 0;
            }
        }
        int ans = 0;
        for(int i = 0; i < arr.length; i++) ans += arr[i];
        return ans;
    }
}
