class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)
        ones = ret = 0
        lidx = -1

        for i in range(n):
            if s[i] == "1":
                if lidx != -1 and s[i - 1] == "0":
                    ret += ones
                ones += 1
                lidx = i

        if ones > 0 and s[n - 1] == "0":
            ret += ones

        return ret
