class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, found = 0;
        bool check = true;
        for (int i = 0; i < s.size(); i++) {
            if (s[i] == '1') {
                found++;
                check = true;
            } else {
                if (check)
                    ans += found;
                check = false;
            }
        }
        return ans;
    }
};
