class Solution {
    public int maxOperations(String s) {
        char[] array = s.toCharArray();
        int index = array.length - 1, result = 0;
        while (index >= 0 && array[index] == '1')
            index--;
        int segment = 0;
        while (index >= 0) {
            while (index >= 0 && array[index] == '0')
                index--;
            segment++;
            int count = 0;
            while (index >= 0 && array[index] == '1') {
                index--;
                count++;
            }
            result += segment * count;
        }
        return result;
    }
}
