class Solution {
public:
    int maxOperations(string s) {
        int res = 0;
        int curOnes = 0;
        for(int i=0; i<s.size(); i++){
            if(s[i] == '1'){
                curOnes++;
                if(i != s.size()-1 && s[i+1] == '0'){
                    res += curOnes;
                }
            }
        }
        return res;
    }
};
