class Solution {
public:
    int maxOperations(string s) {
        int sum = 0;
        int current_len = 0;
        bool find = false;
        for(int i=0;i<s.size();++i) {
            if(s[i]=='1') {
                ++current_len;
                find=true;
            } else {
                if(find) {
                    sum += current_len;
                    find = false;
                }
            }
        }

        return sum;

    }
};
