class Solution {
public:
    int maxOperations(string s) {
        int ans=0,count=0,flag=0;
        for(int i=0;i<s.size();i++){
            if(s[i]=='0') flag=1;
            else{
                if(flag)
                    ans+=count;
                count++;
                flag=0;
            }
        }
        if(flag) ans+=count;
        return ans;
    }
};
