    int maxOperations(string s) {
        int n=s.size();
        int i=0,j=n-1;
        // while(i<n&&s[i]=='0')i++;
        // while(j>=0&&s[j]=='1')j--;
        // if(j<i)return 0;
        int ans=0;
        int z=0,o=0;
        for(int k=0;k<n;k++){
            if(s[k]=='0'){
                ans+=o;
                while(k<n&&s[k]=='0')k++;
                k--;
            }
            else o++;
        }
        cout<<i<<" "<<j<<" "<<z<<" "<<endl;
        return ans;
    }
};
