class Solution {
public:
    int maxOperations(string s) {
        bool f = 1;
        vector<int> pref;
        for(auto it:s){
            if(it == '1'){
                pref.push_back(1);
                f = 0;
            }
            else if(f == 0){
                pref.push_back(0);
                f = 1;
            }
        }

        int ans = 0,sum = 0;
        for(int i = 0;i<pref.size();i++){
            if(pref[i] == 1){
                sum += pref[i];
            }
            else{
                ans += sum;
            }
        }

        return ans;
    }
};
