class Solution {
    public static int maxOperations(String s) {
        int opCount =0;
        int onesFound = 0;

        if(s.charAt(0)=='1'){
            onesFound = 1;
        }

        for(int i = 1;i<s.length();i++){
            if(s.charAt(i)=='0' && s.charAt(i-1)=='1'){
                opCount+=onesFound;
            }else if(s.charAt(i)=='1'){
                onesFound++;
            }
        }

        return opCount;
    }
}
