class Solution:
    def maxOperations(self, s: str) -> int:
        ans = 0
        cnt = 0
        prev = 0
        n = len(s)
        for i, c in enumerate(s):
            if c == '1':
                if prev != i - 1:
                    ans += cnt
                prev = i
                cnt += 1
        if prev != n - 1:
            ans += cnt

        return ans
