class Solution {
public:
    int maxOperations(string s) {
        int ans=0;

        int cnt=0;

        if(s.length()==1){
            return 0;
        }
        if(s[0]=='1'){
            cnt++;
        }
        for(int i=1;i<s.length();i++){
            if(s[i]=='0'){
                continue;
            }
            else{
                if(s[i] == s[i-1]){
                    cnt++;
                }
                else{
                    ans+=cnt;
                    cnt++;
                }
            }
        }

        if(s[s.length()-1]=='0'){
            ans+=cnt;
        }

        return ans;
    }
};
