class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int zero = 0;
        long long ans = 0;
        for (int i = n - 1; i >= 0; i--) {
            if (s[i] == '1') {
                ans += zero;
            } else {
                if (i == n - 1) {
                    zero++;
                } else if (i < n - 1 && s[i + 1] == '1') {
                    zero++;
                }
            }
        }
        return ans;
    }
};
