class Solution:
    def maxOperations(self, s: str) -> int:        
        res = 0
        prev_one = 0
        curr_one = 0
        for c in s:
            if c == "1":
                curr_one += 1
            else:
                if curr_one:
                    res += curr_one + prev_one
                    prev_one += curr_one
                curr_one = 0
        return res
