class Solution {
public:
    int maxOperations(string s) {
        int cnt=0,ans=0,f=0;
        for(int i=0;i<s.length();i++)
        {
            if(s[i]=='1')
            {
                f=0;
                cnt++;
            }
            else
            {
                if(f==0)
                {
                    ans+=cnt;
                    f=1;
                }
            }
        }
        return ans;
    }
};
