class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int cur = 0;

        for(int i = 0; i < s.size();){
            if(s[i] == '1'){
                while(i < s.size() && s[i] == '1'){
                    ++i, ++cur;
                }
            }else{
                while(i < s.size() && s[i] == '0'){
                    ++i;
                }
                ans += cur;
            }

        }

        return ans;
    }
};
