class Solution:
    def maxOperations(self, s: str) -> int:
        indices = [i for i, char in enumerate(s) if char == '1']
        indices.append(len(s))

        operations = 0

        for i in range(len(indices)-1):
            if indices[i+1]!=indices[i]+1:
                operations+=i+1

        return operations
