class Solution {
public:
    int maxOperations(string s) {
        int ans=0,cnt1=0;
        int n=s.length();
        for(int i=0;i<n-1;i++){
            if(s[i]=='1') cnt1++;
            if(s[i]=='0' and s[i+1]=='1') ans+=cnt1;
        }
        if(s.back()=='0')ans+=cnt1;
        return ans;
    }
};
