class Solution {
    public int maxOperations(String s) {
        s = new StringBuilder(s).reverse().toString();
        int n = s.length();
        int ans = 0;
        int cnt0 = 0;
        int i = 0;
        while (i < n) {
            // 分组循环
            int st = i;
            for (i++; i < n && s.charAt(i) == s.charAt(st); i++) {
            }
            if (s.charAt(st) == '1') {
                ans += (i - st) * cnt0;
            } else {
                cnt0++;
            }
        }
        return ans;
    }
}
