int maxOperations(char* s) {
    int sum1 = 0, status = 0, result = 0;
    int i = 0;
    for(; s[i] != '\0'; i++){
        if(s[i] == '1'){
            sum1 ++;
            status = 1;
        }
        else if (status == 1){
            result += sum1;
            status = 0;
        }
    }
    return result;
}
